const fs = require("fs");
const path = require("path");

const CONTRACTS = [];

for (const FILE of fs.readdirSync(
    path.join(
        __dirname,
        "contracts"
    )
)) {
    if (FILE.endsWith(".contract.json")) {
        const CONTRACT = JSON.parse(fs.readFileSync(
            path.join(
                __dirname,
                "contracts",
                FILE
            )
        ).toString());
        CONTRACTS.push(CONTRACT);
    }
}

module.exports = CONTRACTS.sort((a, b) => a.id > b.id);
